import base64
from PIL import Image
import io

# IMG-DECODING
# das kommt vom frontend
data_url = "data:image/jpeg;base64,"

# 1. das "data:image/jpeg;base64," prefix entfernen
base64_string = data_url.split(",")[1]

# 2. base64 zu bytes decodieren
image_bytes = base64.b64decode(base64_string)

# 3. mit pillow nen image-objekt erstellen
image = Image.open(io.BytesIO(image_bytes))

# jetzt könnt ihr mit dem image arbeiten:
width, height = image.size
print(f"Bildgröße: {width}x{height}")

# oder direkt in numpy konvertieren (z.b. für opencv)
import numpy as np
image_array = np.array(image)
#print(image_array[0])

# IMG-AUFTEILUNG

# Festlegung der Anzahl der "Blöcke" (statisch)
num_pixelation_x = 20
num_pixelation_y = 20

# Höhe und breite des Bildes in Variablen speichern 
height, width = image_array.shape[:2]

# Größe der "Pixel-Blöcke" berechnen (lieber direkt in change_image?)
height_block_pix = height // num_pixelation_x
width_block_pix = width // num_pixelation_y

# eigentliche Veränderung 
#def changeImage(num_pixelation_x, num_pixelation_y, pix_area_width, pix_area_height):


def pixelate(image: np.ndarray, block_size: int) -> np.ndarray:
    """
    Pixelates an image using block averaging.

    Parameters:
        image (np.ndarray): Input image (H, W, 3)
        block_size (int): Size of square block

    Returns:
        np.ndarray: Pixelated image
    """
    #h, w, c = image.shape
    output = image.copy()

    for y in range(0, height, height_block_pix):
        for x in range(0, width, width_block_pix):
            block = image[y:y+height_block_pix, x:x+width_block_pix]
            print(block)

            # Compute mean RGB value
            mean_color = block.mean(axis=(0, 1))

            # Assign mean color to the block
            output[y:y+height_block_pix, x:x+width_block_pix] = mean_color
            
    return output.astype(np.uint8)

